import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useParams, useHistory} from 'react-router-dom'
import {useForm} from 'react-hook-form'

const EditProduct = ({products, onUpdate}) => {
    const {handleSubmit, register, errors} = useForm();
    let history = useHistory();
    let {id} = useParams();

    const product = products.find(product => product.id === id);
    const [currentProduct, setCurrentProduct] = useState(product);
    const onHandleSubmit = () => {
        onUpdate(currentProduct);
        history.push('/admin/products');
    }

    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    

    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Edit Product</h1>
            <form onSubmit={handleSubmit(onHandleSubmit)} className="w-50">
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" value={currentProduct.name} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Image</label><br />
                    <img src={products.image} width="100px"/>
                    <input type="text" className="form-control" name="image" value={currentProduct.image} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" name="price" value={currentProduct.price} onChange={onHandleChange}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
