import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useParams, useHistory} from 'react-router-dom'
import {useForm} from 'react-hook-form'

const EditProduct = ({products, onGet, onUpdate}) => {
    const {handleSubmit, register, errors} = useForm();
    let history = useHistory();
    let {id} = useParams();
    onGet(id);

    const onHandleSubmit = (data) => {
        onUpdate(data);
        // history.push("/admin/products");
    }

    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Edit Product</h1>
            <form onSubmit={handleSubmit(onHandleSubmit)} className="w-50">
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" value={products.name}/>
                </div>
                <div className="form-group">
                    <label>Image</label><br />
                    <img src={products.image} width="100px"/>
                    <input type="text" className="form-control" name="image" value={products.image}/>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" name="price" value={products.price}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
