import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import Routers from './routers'
import apiRequest from './api/productApi';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const {data} = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('Failed to request API: ', error)
      }
    }
    getProducts()
  }, []);

  const onHandleAddProduct = async (product) => {
    try {
      const {data} = await apiRequest.create(product);
      setProducts([
        ...products,
        data
      ])
    } catch (error) {
      console.log('Failed to request API: ', error)
    }
  }

  const onHandleUpdateProduct = (updateProduct) => {
    const newProducts = products.map(product => (
      product.id === updateProduct.id ? updateProduct : product  // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
    ));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }

  const onHandleRemoveProduct = async (id) => {
    let history = useHistory();
    try {
      const {data} = await apiRequest.remove(id);
      const newProducts = products.filter(product => product.id !== data.id);
      setProducts(newProducts);
      history.push("/admin/products");
    } catch (error) {
      console.log('failed to request API: ', error)
    }

  }
  return (
    <div className="App">
      <Routers 
        products={products} 
        onRemoveProduct={onHandleRemoveProduct} 
        onAddProduct={onHandleAddProduct} 
        onUpdateProduct={onHandleUpdateProduct}
      />
    </div>
  )

}
export default App;
