import React, {useState, useEffect} from 'react'
import PropTypes from 'prop-types'
import {useParams, useHistory} from 'react-router-dom'
import {useForm} from 'react-hook-form'
import categoryApi from '../../../../api/productApi'

const EditCategory = ({categories, onUpdate}) => {
    const {handleSubmit, register, errors} = useForm();
    let history = useHistory();
    let {id} = useParams();

    const [category, setCategory] = useState({})

    const loadCategory = async () => {
        const {data} = await categoryApi.get(id);
        setCategory(data);
    }

    useEffect(() => {
        loadCategory();
    }, []);

    const onHandleChange = e => {
        setCategory({...category, [e.target.name]: e.target.value});
    }

    const onHandleSubmit = (data) => {
        onUpdate(id, data);
        history.push('/admin/categories');
    }

    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Edit Category</h1>
            <form onSubmit={handleSubmit(onHandleSubmit)} className="w-50">
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" value={category.name} onChange={onHandleChange}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditCategory.propTypes = {
    products: PropTypes.array
}

export default EditCategory
