        e.preventDefault();
        onEdit(valueInput);
        history.push("/admin/products");
    }
    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Edit Product</h1>
            <form onSubmit = {onHandleSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" value={product.name} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Image</label><br />
                    <img src={product.image} width="100px"/>
                    <input type="text" className="form-control" name="image" value={product.image} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" name="price" value={product.price} onChange={onHandleChange}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
