import React, {useState, useEffect} from 'react'
import PropTypes from 'prop-types'
import { Swiper, SwiperSlide } from 'swiper/react'
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper'
import 'swiper/swiper.scss'
import 'swiper/components/navigation/navigation.scss'
import 'swiper/components/pagination/pagination.scss'
import {Link, useParams} from 'react-router-dom'
import productApi from '../../../../api/productApi'

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

const ShopByCate = ({categories}) => {
    let { id } = useParams();
    const [productsByCate, setProductsByCate] = useState([]);

    const loadProductsByCate = async () => {
        const {data} = await productApi.getByCate(id);
        setProductsByCate(data);
    }

    useEffect(() => {
        loadProductsByCate();
    }, []);

    return (
        <div>
            {id}
            {categories.map(({ id, name }) => (
                <li className="mb-1"><Link to={`${id}`} className="d-flex"><span>{name}</span> <span className="text-black ml-auto">(2,124)</span></Link></li>
            ))}
            {productsByCate.map(({ id, cate_id, name, image, price }, index) => (
                <div className="col-lg-6 col-md-6 item-entry mb-4">
                    <Link to={`product/${id}`} className="product-item md-height bg-gray d-block">
                        <img src={image} alt="Image" className="img-fluid" />
                    </Link>
                    <h2 className="item-title"><Link to={`product/${id}`}>{name}</Link></h2>
                    <strong className="item-price"><del>$46.00</del> ${price}</strong>
                    <div className="star-rating">
                        <span className="icon-star2 text-warning" />
                        <span className="icon-star2 text-warning" />
                        <span className="icon-star2 text-warning" />
                        <span className="icon-star2 text-warning" />
                        <span className="icon-star2 text-warning" />
                    </div>
                </div>     
            ))}
        </div>
    )
}

ShopByCate.propTypes = {

}

export default ShopByCate
